import React from 'react'
import { makeStyles } from '@material-ui/core/styles';
import { TextField, Box, Grid } from '@mui/material'

const useStyles = makeStyles((theme) => ({
    textField: {
      width: '100%',
      [theme.breakpoints.up('sm')]: {
          width: '50ch'
      }
    },
}));

class DetectBar extends React.Component {

}

class TranslatedBar extends React.Component {

}

function DetectBox () {
    const classes = useStyles();
      return (
              <Box component="form" noValidate autoComplete="off">
                  <div>
                      <TextField
                        id="outlined-multiline-static"
                        label="Detect Language"
                        multiline
                        rows={9}
                        className={classes.textField}
                      />
                  </div>   
              </Box>
          );
  }

  function TranslatedBox () {
    const classes = useStyles();
      return (
              <Box component="form" noValidate autoComplete="off">
                  <div>
                      <TextField
                        id="outlined-multiline-static"
                        label="Translated Language"
                        multiline
                        rows={9}
                        className={classes.textField}
                      />
                  </div>   
              </Box>
          );
  }

  function Translator () {
      return (
              <Grid container justify="center" alignItems="center" style={{minHeight:"100vh"}} spacing={3}>
                  <Grid item xs={12} sm={6} align="center" justify="center">
                      <DetectBox />
                  </Grid>
                  <Grid item xs={12} sm={6} align="center" justify="center">
                      <TranslatedBox />
                  </Grid>
              </Grid>
          );
  }

  export default Translator
