import React from 'react'
import { TextField, Box, Grid} from '@mui/material'
import { Container } from '@mui/system';


class LanguageBar extends React.Component {}


class DetectBox extends React.Component {

    render() {
        return (
            <Box component="form" noValidateautoComplete="off">
                <TextField
                id="outlined-multiline-static"
                placeholder="Type Here"
                multiline
                fullWidth
                rows={9}
                /> 
            </Box>      
        );
    }
}

class TranslatedBox extends React.Component {

    render() {
        return (
            <Box component="form" noValidateautoComplete="off">
                <TextField
                id="outlined-multiline-static"
                placeholder="Translation"
                multiline
                disabled
                fullWidth
                rows={9}
                />    
            </Box>
        );
    }
}


class Translator extends React.Component {
    render() {
        return (
            <Container>
                <Grid container  alignItems='center' justify='center' style={{minHeight: '100vh'}}>
                    <Grid container rowSpacing={1} columnSpacing={1} maxWidth='50vw'>
                        <Grid item xs={12}>
                            <Box sx={{ bgcolor: '#cfe8fc', height: '10vh' }} />
                        </Grid>
                        <Grid item xs={6}>
                            <DetectBox />
                        </Grid>
                        <Grid item xs={6}>
                            <TranslatedBox />
                        </Grid>     
                    </Grid>    
                </Grid>
            </Container>


        );
    }
}


export default Translator
