import React from 'react'
import { TextField, Box, Grid } from '@mui/material'

class LanguageBar extends React.Component {
    render () {
        return (
            <div></div>
        );
    }
}

function Switch(props) {
    return (
        <div></div>
    );
}

class DropdownLanguageChoice extends React.Component {
    render () {
        return (
            <div></div>
        );
    }
}


class DetectBox extends React.Component {

    render() {
        return (
            <>
                <Box component="form" sx={{'& .MuiTextField-root': { width: '50ch' },}} noValidateautoComplete="off">
                    <TextField
                    id="outlined-multiline-static"
                    label="Detect Language"
                    multiline
                    rows={9}
                    /> 
                </Box>
            </>

        );
    }
}

class TranslatedBox extends React.Component {

    render() {
        return (
            <>
                <Box component="form" sx={{'& .MuiTextField-root': { m: 1, width: '50ch' },}} noValidateautoComplete="off">
                    <TextField
                    id="outlined-multiline-static"
                    label="Translated Language"
                    multiline
                    rows={9}
                    />    
                </Box>
            </>

        );
    }
}


class Translator extends React.Component {
    render() {
        return (
            <Grid container justify="center" alignItems="center" style={{minHeight:"100vh"}}>
                <Grid item xs={6} align="center" >
                    <DetectBox />
                </Grid>
                <Grid item xs={6} align="center" >
                    <TranslatedBox />
                </Grid>
            </Grid>
        );
    }

}

export default Translator
