import React from 'react'
import { TextField, Box, Item, AppBar, Toolbar, Typography} from '@mui/material'
import { Container, Stack } from '@mui/system';


class LanguageBar extends React.Component {}


class DetectBox extends React.Component {

    render() {
        return (
            <>
                <Box component="form" sx={{'& .MuiTextField-root': { width: '50ch',
                '@media (max-width: 600px)': {
                  width: '30ch',
                },
                '@media (min-width: 600px) and (max-width: 900px)': {
                  width: '40ch',
                },
                '@media (min-width: 900px)': {
                  width: '60ch',
                },
                },}} noValidateautoComplete="off">
                    <TextField
                    id="outlined-multiline-static"
                    placeholder="Type Here"
                    multiline
                    rows={9}
                    /> 
                </Box>
            </>

        );
    }
}

class TranslatedBox extends React.Component {

    render() {
        return (
            <>
                <Box component="form" sx={{'& .MuiTextField-root': { width: '50ch',
                '@media (max-width: 600px)': {
                  width: '30ch',
                },
                '@media (min-width: 600px) and (max-width: 900px)': {
                  width: '40ch',
                },
                '@media (min-width: 900px)': {
                  width: '60ch',
                },
                },}} noValidateautoComplete="off">
                    <TextField
                    id="outlined-multiline-static"
                    placeholder="Translation"
                    multiline
                    disabled
                    rows={9}
                    />    
                </Box>
            </>

        );
    }
}


class Translator extends React.Component {
    render() {
        return (
            <>
                <Stack
                    direction="column"
                    justifyContent="center"
                    alignItems="center"
                    spacing={0}
                > 
                    <div>
                        <AppBar>
                            <Toolbar>
                                <Typography>Hello</Typography>
                            </Toolbar>
                        </AppBar>
                    </div>
                    <div style={{ position: 'absolute', top:0, bottom:0, left:0, right:0, display: 'flex', justifyContent: 'center', alignItems: 'center'}}>
                        <DetectBox />
                        <TranslatedBox />
                    </div>   
                </Stack>
            </>   
        );
    }
}

export default Translator
