import React from 'react'
import { TextField, Box, Grid, Button, Typography, Autocomplete} from '@mui/material'

const languages = [
    { name: 'English', code: 'en' },
    { name: 'French', code: 'fr' }
];

class LanguageBar extends React.Component {
    render() {
        return (
            <Box sx={{ bgcolor: '#ABE3FF', height: '10vh', border: 1, borderRadius: 2 }}>
                <Grid
                    container
                    direction="row"
                    justifyContent="flex-start"
                    alignItems="center"
                    spacing={12}
                >
                    <Grid item sx={2}>
                        <Typography>Detected Language</Typography>
                        <Autocomplete
                            disablePortal
                            id="combo-box-demo"
                            options={languages}
                            sx={{ width: 300 }}
                            renderInput={(params) => <TextField {...params} label="Movie" />}
                        />
                    </Grid>
                    <Grid item sx={2}>
                        <Button>Hello</Button>
                    </Grid>
                </Grid>
            </Box>
        );
    }
}


class DetectBox extends React.Component {

    render() {
        return (
            <Box component="form" noValidateautoComplete="off">
                <TextField
                id="outlined-multiline-static"
                placeholder="Type Here"
                multiline
                fullWidth
                rows={9}
                /> 
            </Box>      
        );
    }
}

class TranslatedBox extends React.Component {

    render() {
        return (
            <Box component="form" noValidateautoComplete="off">
                <TextField
                id="outlined-multiline-static"
                placeholder="Translation"
                multiline
                disabled
                fullWidth
                rows={9}
                />    
            </Box>
        );
    }
}


class Translator extends React.Component {
    render() {
        return (
            <Grid container alignItems='center' justifyContent='center' style={{minHeight: '80vh'}}>
                <Grid container rowSpacing={1} columnSpacing={1} maxWidth='70vw'>
                    <Grid item xs={12}>
                        <LanguageBar />
                    </Grid>
                    <Grid item xs={6}>
                        <DetectBox />
                    </Grid>
                    <Grid item xs={6}>
                        <TranslatedBox />
                    </Grid>     
                </Grid>    
            </Grid>  
        );
    }
}


export default Translator
