import React from 'react'
import { TextField, Box, CssBaseline} from '@mui/material'
import { Container } from '@mui/system';


class LanguageBar extends React.Component {

}


class DetectBox extends React.Component {

    render() {
        return (
            <>
                <Box component="form" sx={{'& .MuiTextField-root': { width: '50ch',
                '@media (max-width: 600px)': {
                  width: '30ch',
                },
                '@media (min-width: 600px) and (max-width: 900px)': {
                  width: '40ch',
                },
                '@media (min-width: 900px)': {
                  width: '60ch',
                },
                },}} noValidateautoComplete="off">
                    <TextField
                    id="outlined-multiline-static"
                    placeholder="Type Here"
                    multiline
                    rows={9}
                    /> 
                </Box>
            </>

        );
    }
}

class TranslatedBox extends React.Component {

    render() {
        return (
            <>
                <Box component="form" sx={{'& .MuiTextField-root': { width: '50ch',
                '@media (max-width: 600px)': {
                  width: '30ch',
                },
                '@media (min-width: 600px) and (max-width: 900px)': {
                  width: '40ch',
                },
                '@media (min-width: 900px)': {
                  width: '60ch',
                },
                },}} noValidateautoComplete="off">
                    <TextField
                    id="outlined-multiline-static"
                    placeholder="Translation"
                    multiline
                    disabled
                    rows={9}
                    />    
                </Box>
            </>

        );
    }
}


class Translator extends React.Component {
    render() {
        return (
            <>
                <CssBaseline />
                <Container style={{display: 'flex', alignItems: 'center', justifyContent: 'center'}}>
                    <Box sx={{ bgcolor: '#cfe8fc', height: '8vh', borderColor:'black', borderWidth: '1px', borderStyle: 'solid', width: '100%'}} />
                    <div style={{ display: 'flex', alignItems: 'center', justifyContent: 'center'}}>
                        <DetectBox />
                        <TranslatedBox />
                    </div>
                </Container>
            </>   
        );
    }
}


export default Translator
