import React from 'react'
import { TextField, Box, CssBaseline, AppBar, Toolbar, Select, MenuItem, Typography, Autocomplete } from '@mui/material'

class LanguageBar extends React.Component {
    render () {
        return (
            <div></div>
        );
    }
}

function Switch(props) {
    return (
        <div></div>
    );
}

class DropdownLanguageChoice extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            selectedValue: '',
            textValue: ''
        };
    }

    handleSelectChange = (event) => {
        this.setState({ selectedValue: event.target.value });
        this.setState({ textValue: event.target.value });
    }

    render () {
        return (
            <>
                <CssBaseline>
                    <Toolbar>
                        <Select
                            value={this.state.selectedValue}
                            onChange={this.handleSelectChange}
                            >
                                <MenuItem value={'Option 1'}>Option 1</MenuItem>
                                <MenuItem value={'Option 2'}>Option 2</MenuItem>
                                <MenuItem value={'Option 3'}>Option 3</MenuItem>
                            </Select>
                            <TextField
                            value={this.state.textValue}
                            disabled
                        />
                    </Toolbar>
                </CssBaseline>
            </>
        );
    }
}


class DetectBox extends React.Component {

    render() {
        return (
            <>
                <Box component="form" sx={{'& .MuiTextField-root': { width: '50ch',
                '@media (max-width: 600px)': {
                  width: '30ch',
                },
                '@media (min-width: 600px) and (max-width: 900px)': {
                  width: '40ch',
                },
                '@media (min-width: 900px)': {
                  width: '60ch',
                },
                },}} noValidateautoComplete="off">
                    <TextField
                    id="outlined-multiline-static"
                    placeholder="Type Here"
                    multiline
                    rows={9}
                    /> 
                </Box>
            </>

        );
    }
}

class TranslatedBox extends React.Component {

    render() {
        return (
            <>
                <Box component="form" sx={{'& .MuiTextField-root': { width: '50ch',
                '@media (max-width: 600px)': {
                  width: '30ch',
                },
                '@media (min-width: 600px) and (max-width: 900px)': {
                  width: '40ch',
                },
                '@media (min-width: 900px)': {
                  width: '60ch',
                },
                },}} noValidateautoComplete="off">
                    <TextField
                    id="outlined-multiline-static"
                    placeholder="Translation"
                    multiline
                    disabled
                    rows={9}
                    />    
                </Box>
            </>

        );
    }
}


class Translator extends React.Component {
    constructor(props) {
      super(props);
      this.state = {
        options: [
          { title: 'Option 1' },
          { title: 'Option 2' },
          { title: 'Option 3' }
        ],
        autocompleteValue: '',
      };
    }

    handleAutocompleteChange = (event, newValue) => {
      this.setState({ autocompleteValue: newValue });
    };

    render() {
      return (
          <div>
              {/* Secondary Navigation Bar */}
              <AppBar position="static" color="primary">
                  <Toolbar>
                      <Typography variant="h6">
                          Secondary Navigation
                      </Typography>
                      <Autocomplete
                          id="combo-box-demo"
                          options={this.state.options}
                          value={this.state.autocompleteValue}
                          onChange={this.handleAutocompleteChange}
                          getOptionLabel={(option) => option.title}
                          style={{ width: 300 }}
                          renderInput={(params) => <TextField {...params} label="Combo box" variant="outlined" />}
                      />
                  </Toolbar>
              </AppBar>

              <div style={{ position: 'absolute', top:0, bottom:0, left:0, right:0, display: 'flex', justifyContent: 'center', alignItems: 'center'}}>
                  <DetectBox />
                  <TranslatedBox />
              </div>
          </div>
      );
    }
  }




export default Translator
