import React from 'react'
import { TextField, Box, Grid, Autocomplete} from '@mui/material'
import useMediaQuery from '@mui/material/useMediaQuery';
import SwapHorizontalCircleRoundedIcon from '@mui/icons-material/SwapHorizontalCircleRounded';

const languages = [
    { label: 'English', code: 'en' },
    { label: 'French', code: 'fr' }
];

const LanguageBar = () => {
    const matches = useMediaQuery('(min-width:600px)');
    return (
        <Box sx={{ bgcolor: '#ABE3FF', height: '10vh', border: 1, borderRadius: 2 }}>
            <Grid container direction="row" justifyContent="space-between" alignItems="center">
                <Grid item>
                    <Autocomplete
                        fullWidth
                        id="combo-box-detect"
                        options={languages}
                        sx={{ width: matches ? '30ch' : '50ch', m: 1 }}
                        renderInput={(params) => <TextField {...params} label="Base Language" />}
                    />
                </Grid>
                <Grid item>
                    <SwapHorizontalCircleRoundedIcon fontSize='large' alignItems='center' sx={{ color: '#fff', border: 2, borderRadius: '50%', borderColor: '#fff', backgroundColor: '#000'  }} />
                </Grid>
                <Grid item>
                    <Autocomplete
                        disablePortal
                        id="combo-box-translate"
                        options={languages}
                        sx={{ width: matches ? '30ch' : '50ch', m: 1 }}
                        renderInput={(params) => <TextField {...params} label="Converted Language" />}
                    />
                </Grid>
            </Grid>
        </Box>
    );
}


class DetectBox extends React.Component {

    render() {
        return (
            <Box component="form" noValidateautoComplete="off">
                <TextField
                id="outlined-multiline-static"
                placeholder="Type Here"
                multiline
                fullWidth
                rows={9}
                /> 
            </Box>      
        );
    }
}

class TranslatedBox extends React.Component {

    render() {
        return (
            <Box component="form" noValidateautoComplete="off">
                <TextField
                id="outlined-multiline-static"
                placeholder="Translation"
                multiline
                disabled
                fullWidth
                rows={9}
                />    
            </Box>
        );
    }
}


class Translator extends React.Component {
    render() {
        return (
            <Grid container alignItems='center' justifyContent='center' style={{minHeight: '80vh'}}>
                <Grid container rowSpacing={1} columnSpacing={1} maxWidth='70vw'>
                    <Grid item xs={12}>
                        <LanguageBar />
                    </Grid>
                    <Grid item xs={6}>
                        <DetectBox />
                    </Grid>
                    <Grid item xs={6}>
                        <TranslatedBox />
                    </Grid>     
                </Grid>    
            </Grid>  
        );
    }
}


export default Translator
