import React from 'react'
import { TextField, Box, CssBaseline, Container} from '@mui/material'


class LanguageBar extends React.Component {}


class DetectBox extends React.Component {

    render() {
        return (
            <>
                <Box component="form" sx={{'& .MuiTextField-root': { width: '50ch',
                '@media (max-width: 600px)': {
                  width: '30ch',
                },
                '@media (min-width: 600px) and (max-width: 900px)': {
                  width: '40ch',
                },
                '@media (min-width: 900px)': {
                  width: '60ch',
                },
                },}} noValidateautoComplete="off">
                    <TextField
                    id="outlined-multiline-static"
                    placeholder="Type Here"
                    multiline
                    rows={9}
                    /> 
                </Box>
            </>

        );
    }
}

class TranslatedBox extends React.Component {

    render() {
        return (
            <>
                <Box component="form" sx={{'& .MuiTextField-root': { width: '50ch',
                '@media (max-width: 600px)': {
                  width: '30ch',
                },
                '@media (min-width: 600px) and (max-width: 900px)': {
                  width: '40ch',
                },
                '@media (min-width: 900px)': {
                  width: '60ch',
                },
                },}} noValidateautoComplete="off">
                    <TextField
                    id="outlined-multiline-static"
                    placeholder="Translation"
                    multiline
                    disabled
                    rows={9}
                    />    
                </Box>
            </>

        );
    }
}


class Translator extends React.Component {
    render() {
        return (
            <>
                <CssBaseline />
                <Container style={{display: 'flex', alignItems: 'center', justifyContent: 'center', height: '100vh', position: 'relative'}}>
                    <Box style={{ backgroundColor: '#cfe8fc', height: '8vh', borderColor:'black', borderWidth: '1px', borderStyle: 'solid', width: '100%', position: 'absolute', top: 0, left: 0, right: 0}} />
                    <div style={{ display: 'flex', alignItems: 'center', justifyContent: 'center', width: '100%'}}>
                        <DetectBox />
                        <TranslatedBox />
                    </div>
                </Container>
            </>   
        );
    }
}


export default Translator
