import React from 'react'
import { TextField, Box, Grid} from '@mui/material'


class LanguageBar extends React.Component {}


class DetectBox extends React.Component {

    render() {
        return (
            <>
                <Box component="form" sx={{'& .MuiTextField-root': { width: '50ch'}}} noValidateautoComplete="off">
                    <TextField
                    id="outlined-multiline-static"
                    placeholder="Type Here"
                    multiline
                    rows={9}
                    /> 
                </Box>
            </>

        );
    }
}

class TranslatedBox extends React.Component {

    render() {
        return (
            <>
                <Box component="form" sx={{'& .MuiTextField-root': { width: '50ch'}}} noValidateautoComplete="off">
                    <TextField
                    id="outlined-multiline-static"
                    placeholder="Translation"
                    multiline
                    disabled
                    rows={9}
                    />    
                </Box>
            </>

        );
    }
}


class Translator extends React.Component {
    render() {
        return (
            <Grid container align='center' justify='center' rowSpacing={0} columnSpacing={0}>
                <Grid item xs={12}>
                    <Box sx={{ bgcolor: '#cfe8fc', height: '10vh' }} />
                </Grid>
                <Grid item xs={6}>
                    <DetectBox />
                </Grid>
                <Grid item xs={6}>
                    <TranslatedBox />
                </Grid>
            </Grid>        
        );
    }
}

/**
            <Container align='center' justify='center' style={{minHeight: '100vh'}}>
                <Grid container align='center' justify='center' rowSpacing={0} columnSpacing={0}>
                    <Grid item xs={12}>
                        <Box sx={{ bgcolor: '#cfe8fc', height: '10vh' }} />
                    </Grid>
                    <Grid item xs={6}>
                        <DetectBox />
                    </Grid>
                    <Grid item xs={6}>
                        <TranslatedBox />
                    </Grid>
                </Grid>
            </Container>
 */

export default Translator
