import React from 'react'
import { TextField, Box, AppBar, Input, makeStyles } from '@mui/material'


const useStyles = makeStyles((theme) => ({
    appBar: {

      color: 'black',
      boxShadow: 'none'
    },
  }));

function LanguageHeader() {
    const classes = useStyles();

    return (
      <AppBar position="static" className={classes.appBar}>
        <Input placeholder="Search..." />
      </AppBar>
    );
}

class DetectBox extends React.Component {

    render() {
        return (
            <>
                <Box component="form" sx={{'& .MuiTextField-root': { width: '50ch',
                '@media (max-width: 600px)': {
                  width: '30ch',
                },
                '@media (min-width: 600px) and (max-width: 900px)': {
                  width: '40ch',
                },
                '@media (min-width: 900px)': {
                  width: '60ch',
                },
                },}} noValidateautoComplete="off">
                    <TextField
                    id="outlined-multiline-static"
                    placeholder="Type Here"
                    multiline
                    rows={9}
                    /> 
                </Box>
            </>

        );
    }
}

class TranslatedBox extends React.Component {

    render() {
        return (
            <>
                <Box component="form" sx={{'& .MuiTextField-root': { width: '50ch',
                '@media (max-width: 600px)': {
                  width: '30ch',
                },
                '@media (min-width: 600px) and (max-width: 900px)': {
                  width: '40ch',
                },
                '@media (min-width: 900px)': {
                  width: '60ch',
                },
                },}} noValidateautoComplete="off">
                    <TextField
                    id="outlined-multiline-static"
                    placeholder="Translation"
                    multiline
                    disabled
                    rows={9}
                    />    
                </Box>
            </>

        );
    }
}


class Translator extends React.Component {
    render() {
        return (
            <div style={{ position: 'absolute', top:0, bottom:0, left:0, right:0, display: 'flex', justifyContent: 'center', alignItems: 'center'}}>
                <DetectBox />
                <TranslatedBox />
            </div>
        );
    }
}

export default Translator
