import React, { useState } from 'react'
import axios from 'axios';
import { TextField, Box, Grid, Autocomplete, InputAdornment, IconButton, makeStyles} from '@mui/material'
import SwapHorizontalCircleRoundedIcon from '@mui/icons-material/SwapHorizontalCircleRounded';
import VolumeUpIcon from '@mui/icons-material/VolumeUp';
import MicIcon from '@mui/icons-material/Mic';
import { grey } from '@mui/material/colors';
import useMediaQuery from '@mui/material/useMediaQuery';


const languages = [
    { label: 'English'},
    { label: 'French'},
    { label: 'German'},
    { label: 'Japanese'}
];

const useStyles = makeStyles((theme) => ({
    swap: {
        color: '#fff', 
        border: 2, 
        borderRadius: '50%', 
        borderColor: '#fff', 
        backgroundColor: '#000'
    },
    thing : {
        color: '#aaa'
    }
}));


function SwapLanguage(props) {

    const classes = useStyles();

    const handleSwap = () => {
        props.setBaseLang(props.convertedLang);
        props.setConvertedLang(props.baseLang);
    }

    return(
        <IconButton onClick={handleSwap}>
            <SwapHorizontalCircleRoundedIcon 
                fontSize='large' 
                className={classes.thing}
            />
        </IconButton>
    );
}


function LanguageBar() {
    const verySmallScreen = useMediaQuery('(max-width:649px)');
    const smallScreen = useMediaQuery('(min-width:650px) and (max-width:899px)');
    const mediumScreen = useMediaQuery('(min-width:900px) and (max-width:1200px)');
    const [baseLang, setBaseLang] = useState(languages[0]);
    const [convertedLang, setConvertedLang] = useState(languages[1]);

    return (
      <Box sx={{ bgcolor: '#ABE3FF', height: '10vh', border: 1, borderRadius: 2 }}>
        <Grid container direction='row' justifyContent='space-between' alignItems='center'>
          <Grid item>
            <Autocomplete
              fullWidth
              id="combo-box-detect"
              options={languages}
              value={baseLang}
              onChange={(_, newValue) => {
                setBaseLang(newValue);
              }}
              sx={{ 
                width: 
                verySmallScreen ? '10ch' : 
                smallScreen ? '20ch' : 
                mediumScreen ? '30ch' : '40ch', 
                m: 1 
              }}
              renderInput={(params) => <TextField {...params} />}
            />
          </Grid>
          <Grid item>
            <SwapLanguage 
              baseLang={baseLang} 
              setBaseLang={setBaseLang} 
              convertedLang={convertedLang} 
              setConvertedLang={setConvertedLang} 
            />
          </Grid>
          <Grid item>
            <Autocomplete
              disablePortal
              id="combo-box-translate"
              options={languages}
              value={convertedLang}
              onChange={(_, newValue) => {
                setConvertedLang(newValue);
              }}
              sx={{ 
                width: verySmallScreen ? '10ch' : 
                smallScreen ? '20ch' : 
                mediumScreen ? '30ch' : '40ch', 
                m: 1 
              }}
              renderInput={(params) => <TextField {...params} />}
            />
          </Grid>
        </Grid>
      </Box>
    );
}



class DetectBox extends React.Component {

    render() {
        return (
            <Box component="form" >
                <TextField
                id="outlined-multiline-static"
                placeholder='Type Here'
                multiline
                fullWidth
                rows={9}
                InputProps={{
                    endAdornment: <InputAdornment position='end' style={{position: 'absolute', bottom: 30, left: 0}}>
                        <IconButton >
                            <MicIcon sx={{ color: grey[900] }}/>
                        </IconButton>
                        <IconButton >
                            <VolumeUpIcon sx={{ color: grey[900] }}/>
                        </IconButton>
                    </InputAdornment>,
                }}
                onKeyPress= {(e) => {
                    if (e.key) {
                      console.log(e.target.value);

                    }
                }}
                /> 
            </Box>      
        );
    }
}


class TranslatedBox extends React.Component {

    render() {
        return (
            <Box component="form">
                <TextField
                id="outlined-multiline-static"
                placeholder='Translation'
                multiline
                disabled
                fullWidth
                rows={9}
                InputProps={{
                    endAdornment: <InputAdornment position='end' style={{position: 'absolute', bottom: 30, left: 0}}>
                        <IconButton >
                            <VolumeUpIcon sx={{ color: grey[900] }}/>
                        </IconButton>
                    </InputAdornment>,
                }}
                />    
            </Box>
        );
    }
}


class Translator extends React.Component {
    render() {
        return (
            <Grid container alignItems='center' justifyContent='center' style={{minHeight: '80vh'}}>
                <Grid container rowSpacing={1} columnSpacing={1} maxWidth='70vw'>
                    <Grid item xs={12}>
                        <LanguageBar />
                    </Grid>
                    <Grid item xs={6}>
                        <DetectBox />
                    </Grid>
                    <Grid item xs={6}>
                        <TranslatedBox />
                    </Grid>     
                </Grid>    
            </Grid>  
        );
    }
}


export default Translator
