import React from 'react'
import { makeStyles } from '@material-ui/core/styles';
import { TextField, Box, Grid } from '@mui/material'

const useStyles = makeStyles((theme) => ({
    textField: {
      width: '100%',
      [theme.breakpoints.up('sm')]: {
          width: '50ch'
      }
    },
}));

class DetectBar extends React.Component {

}

class TranslatedBar extends React.Component {

}

function DetectBox () {
    const classes = useStyles();
      return (
              <Box component="form" noValidate autoComplete="off">
                  <div>
                      <TextField
                        id="outlined-multiline-static"
                        label="Detect Language"
                        multiline
                        rows={9}
                        className={classes.textField}
                      />
                  </div>   
              </Box>
          );
  }

class TranslatedBox extends React.Component {

    render() {
        return (
            <Box component="form" sx={{'& .MuiTextField-root': { m: 1, width: '50ch' },}} noValidateautoComplete="off">
                <div>
                    <TextField
                    id="outlined-multiline-static"
                    label="Translated Language"
                    multiline
                    rows={9}
                    />
                </div>   
            </Box>
        );
    }
}


class Translator extends React.Component {
    render() {
        return (
            <Grid container justify="center" alignItems="center" style={{minHeight:"100vh"}}>
                <Grid item xs={6} align="center" justify="center">
                    <DetectBox />
                </Grid>
                <Grid item xs={6} align="center" justify="center">
                    <TranslatedBox />
                </Grid>
            </Grid>
        );
    }

}

export default Translator
